import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [tableName, setTableName] = useState("");
  const [tableColumns, setTableColumns] = useState([]);
  const [fileUrl, setFileUrl] = useState("");

  useEffect(() => {
    // Load table columns when tableName changes
    if (tableName) {
      axios.get(`/table/${tableName}`).then((res) => {
        setTableColumns(res.data.columns);
      });
    }
  }, [tableName]);

  const handleTableSelection = (event) => {
    setTableName(event.target.value);
    setTableColumns([]);
  };

  const handleColumnChange = (event, index) => {
    const updatedColumns = [...tableColumns];
    updatedColumns[index] = {
      ...updatedColumns[index],
      [event.target.name]: event.target.value,
    };
    setTableColumns(updatedColumns);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const res = await axios.post("/generate-file", {
      tableName,
      columns: tableColumns,
    });
    setFileUrl(res.data.fileUrl);
  };

  return (
    <div>
      <h1>Generate JSON File</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="tableName">Table Name:</label>
        <input
          type="text"
          id="tableName"
          name="tableName"
          value={tableName}
          onChange={handleTableSelection}
        />
        <br />
        {tableColumns.length > 0 && (
          <>
            <h2>Table Columns:</h2>
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Type</th>
                  <th>Length</th>
                </tr>
              </thead>
              <tbody>
                {tableColumns.map((column, index) => (
                  <tr key={index}>
                    <td>{column.name}</td>
                    <td>
                      <select
                        name="type"
                        value={column.type}
                        onChange={(event) => handleColumnChange(event, index)}
                      >
                        <option value="VARCHAR">VARCHAR</option>
                        <option value="INT">INT</option>
                      </select>
                    </td>
                    <td>
                      <input
                        type="number"
                        name="length"
                        value={column.length}
                        onChange={(event) => handleColumnChange(event, index)}
                      />
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </>
        )}
        <br />
        {tableColumns.length > 0 && (
          <button type="submit">Generate File</button>
        )}
      </form>
      {fileUrl && (
        <div>
          <a href={fileUrl} download="output.json">
            Download File
          </a>
        </div>
      )}
    </div>
  );
}

export default App;
