import React, { useState } from "react";
import axios from "axios";
import Columns from "./Columns";

const Users = () => {
  const [inputValue, setInputValue] = useState("");
  const [tableName, setTableName] = useState("");
  const [columns, setColumns] = useState([]);
  const [sourceDefinition, setSourceDefinition] = useState({
    Name: "",
    columns: [],
  });
  const [tableStatus, setTableStatus] = useState();
  const [displayedColumns, setDisplayedColumns] = useState([]);
  // Hide fetch cloumn button by default passing true or false
  const [fetchButton, setFetchButton] = useState(false);
  // Hide show columns by default
  const [showColumns, setShowColumns] = useState(false);
  const handleSubmit = async (event) => {
    event.preventDefault();
    const value = inputValue;
    // Send inputValue to server using fetch or axios
    const response = await axios.post("http://localhost:8800/", { value });
    console.log(response.data);
    setTableName(value);
  };
  const handleChange = (event) => {
    setInputValue(event.target.value);
  };

  const fetchColumns = async () => {
    try {
      const response = await axios.get(`http://localhost:8800/`);
      const columns = response.data.map((col) => ({
        column_name: col.column_name,
        data_type: col.data_type,
      }));
      setTableStatus("");
      setColumns(columns);
      setSourceDefinition({
        ...sourceDefinition,
        columns: columns,
      });
    } catch (error) {
      console.log(error);
      setTableStatus("Table not found");
    }
  };

  const handleColumnNameChange = (event, i) => {
    const newColumns = [...sourceDefinition.columns];
    newColumns[i].column_name = event.target.value;
    setSourceDefinition({
      ...sourceDefinition,
      columns: newColumns,
    });
  };

  const handleDataTypeChange = (event, i) => {
    const newColumns = [...sourceDefinition.columns];
    newColumns[i].data_type = event.target.value;
    setSourceDefinition({
      ...sourceDefinition,
      columns: newColumns,
    });
  };

  const display = () => {
    setDisplayedColumns(sourceDefinition.columns);
  };

  const handleDownload = () => {
    const jsonData = JSON.stringify(sourceDefinition, null, 2);
    const file = new Blob([jsonData], { type: "application/json" });
    const a = document.createElement("a");
    const url = URL.createObjectURL(file);
    a.href = url;
    a.download = `${tableName}.json`;
    document.body.appendChild(a);
    a.click();
    setTimeout(() => {
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);
    }, 0);
  };
  const handleButtonClick = () => {
    fetchColumns();
    setShowColumns(true);
  };
  return (
    <div>
      <h1>Table Name:</h1>
      <p>{tableStatus}</p>
      <form onSubmit={handleSubmit}>
        <input type="text" value={inputValue} onChange={handleChange} />
        <button type="submit" onClick={() => setFetchButton(true)}>
          Submit
        </button>
      </form>
      {fetchButton && (
        <div style={{ backgroundColor: "cyan" }}>
          <p>Ready to fetch {tableName} table. </p>
          <h1>Load Table:</h1>
          <button onClick={handleButtonClick}>Fetch Columns</button>
        </div>
      )}

      {/*Hide display column Section by default  */}
      <br />

      {showColumns && (
        <div>
          <h1>Columns:</h1>
          <ul>
            {columns.map((col, i, index) => (
              <li key={i}>
                <input
                  type="text"
                  value={col.column_name}
                  onChange={(event) => handleColumnNameChange(event, i)}
                />

                <input
                  type="text"
                  value={col.data_type}
                  onChange={(event) => handleDataTypeChange(event, i)}
                />
              </li>
            ))}
          </ul>
          <div>
            <button onClick={display}>Display</button>
            {displayedColumns.map((col, i) => (
              <Columns key={i} column={col} />
            ))}
            <button onClick={handleDownload}>Download JSON</button>
          </div>{" "}
        </div>
      )}
    </div>
  );
};

export default Users;
