import React, { useState } from "react";
import axios from "axios";

const Users = () => {
  const [tableName, setTableName] = useState("");
  const [columns, setColumns] = useState([]);
  const [sourceDefinition, setSourceDefinition] = useState({
    Name: "",
    columns: [],
  });
  const [selectedColumn, setSelectedColumn] = useState("");
  const [newColumnName, setNewColumnName] = useState("");
  const [newColumnType, setNewColumnType] = useState("");

  const fetchColumns = async () => {
    try {
      const response = await axios.get(`http://localhost:8800/`);
      const columns = response.data.map((col) => ({
        column_name: col.column_name,
        data_type: col.data_type,
      }));
      setColumns(columns);
      setSourceDefinition({
        ...sourceDefinition,
        columns: columns,
      });
    } catch (error) {
      console.log(error);
    }
  };

  const handleColumnSelect = (column_name) => {
    setSelectedColumn(column_name);
  };

  const handleColumnChange = async () => {
    try {
      const response = await axios.put(`http://localhost:8800/`, {
        new_column_name: newColumnName,
        new_column_type: newColumnType,
      });
      console.log(response.data); // log response to console
      fetchColumns(); // fetch the updated column metadata
      setSelectedColumn(""); // reset the selected column
      setNewColumnName(""); // reset the new column name
      setNewColumnType(""); // reset the new column type
    } catch (error) {
      console.log(error);
    }
  };

  const handleDownload1 = async () => {
    try {
      // Create a new object with the modified sourceDefinition
      const updatedSourceDefinition = {
        Name: tableName,
        SourceDB: sourceDefinition.SourceDB,
        columns: [...sourceDefinition.columns],
      };
      updatedSourceDefinition.columns = updatedSourceDefinition.columns.map(
        (col) => {
          if (col.column_name === selectedColumn) {
            return {
              ...col,
              // Update the column name and data type based on user input
              column_name: newColumnName,
              data_type: newColumnType,
            };
          }
          return col;
        }
      );

      // Convert the updated sourceDefinition to JSON format
      const json = JSON.stringify(updatedSourceDefinition, null, 2);

      // Download the file
      const url = window.URL.createObjectURL(new Blob([json]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", "output.json");
      document.body.appendChild(link);
      link.click();
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div>
      <h1>Table Name:</h1>
      <input
        type="text"
        value={tableName}
        onChange={(e) => setTableName(e.target.value)}
      />
      <button onClick={fetchColumns}>Fetch Columns</button>
      <br />
      <h1>Columns:</h1>
      <ul>
        {columns.map((col) => (
          <li key={col.column_name}>
            {col.column_name} ({col.data_type})
            <button onClick={() => handleColumnSelect(col.column_name)}>
              Edit
            </button>
          </li>
        ))}
      </ul>
      {selectedColumn && (
        <>
          <h1>Edit Column:</h1>
          <p>Column Name: {selectedColumn}</p>
          <label>
            New Column Name:
            <input
              type="text"
              value={newColumnName}
              onChange={(e) => setNewColumnName(e.target.value)}
            />
          </label>
          <br />
          <label>
            New Column Type:
            <input
              type="text"
              value={newColumnType}
              onChange={(e) => setNewColumnType(e.target.value)}
            />
          </label>
          <br />
          <button onClick={handleColumnChange}>Update Column</button>
          <br />
          <button onClick={handleDownload1}>Download JSON</button>
        </>
      )}
      <br />
    </div>
  );
};

export default Users;
