import React, { useState } from "react";
import axios from "axios";

const Users = () => {
  const [tableName, setTableName] = useState("");
  const [columns, setColumns] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [sourceDefinition, setSourceDefinition] = useState({
    Name: "",
    columns: [],
  });
  const [val, setVal] = useState([]);
  const [tableStatus, setTableStatus] = useState();

  const fetchColumns = async () => {
    try {
      const response = await axios.get(`http://localhost:8800/`);
      const columns = response.data.map((col) => ({
        column_name: col.column_name,
        data_type: col.data_type,
      }));
      setTableStatus("");
      setColumns(columns);
      setSourceDefinition({
        ...sourceDefinition,
        columns: columns,
      });
    } catch (error) {
      console.log(error);
      setTableStatus("Table not found");
    }
  };
  const handleChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const value = inputValue;
    // Send inputValue to server using fetch or axios
    const response = await axios.post("http://localhost:8800/", { value });
    console.log(response.data);
    setTableName(value);
  };

  const handleVal = (value, i) => {
    const allVal = [...val];
    const inputData = [...allVal];
    inputData[i] = value.target.value;
    setVal(inputData);
  };
  console.log(val);
  return (
    <div>
      <h1>Table Name:</h1>
      <p>{tableStatus}</p>
      <form onSubmit={handleSubmit}>
        <input type="text" value={inputValue} onChange={handleChange} />
        <button type="submit">Submit</button>
      </form>
      <p>Ready to fetch {tableName} table.</p>
      <h1>Load Table:</h1>
      <button onClick={fetchColumns}>Fetch Columns</button>
      <br />
      <h1>Columns:</h1>
      <ul>
        {columns.map((col, i) => (
          <li key={col.column_name}>
            {col.column_name} ({col.data_type})
            <input type="text" />
          </li>
        ))}
      </ul>

      <>
        <button>Download JSON</button>
      </>
    </div>
  );
};

export default Users;
