import React, { useState } from "react";
import axios from "axios";

const Users = () => {
  const [tableName, setTableName] = useState("");
  const [columns, setColumns] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [sourceDefinition, setSourceDefinition] = useState({
    Name: "",
    columns: [],
  });
  const [selectedColumn, setSelectedColumn] = useState("");
  const [newColumnName, setNewColumnName] = useState("");
  const [newColumnType, setNewColumnType] = useState("");

  const fetchColumns = async () => {
    try {
      const response = await axios.get(`http://localhost:8800/`);
      const columns = response.data.map((col) => ({
        column_name: col.column_name,
        data_type: col.data_type,
      }));
      setColumns(columns);
      setSourceDefinition({
        ...sourceDefinition,
        columns: columns,
      });
    } catch (error) {
      console.log(error);
    }
  };

  const handleColumnSelect = (column_name) => {
    setSelectedColumn(column_name);
  };

  const handleChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const value = inputValue;
    // Send inputValue to server using fetch or axios
    const response = await axios.post("http://localhost:8800/", { value });
    console.log(response.data);
  };
  const [newColumnValues, setNewColumnValues] = useState({
    columnName: "",
    columnType: "",
  });
  const [columnNames, setColumnNames] = useState([]);
  const handleColumnNameChange = (event) => {
    const newColumnName = event.target.value;
    setNewColumnValues({ ...newColumnValues, columnName: newColumnName });
    setColumnNames([...columnNames, newColumnName]); // add new column name to array
  };

  const handleConfirmClick = () => {
    setColumnNames([...columnNames, newColumnValues.columnName]);
  };

  return (
    <div>
      <h1>Table Name:</h1>
      <form onSubmit={handleSubmit}>
        <input type="text" value={inputValue} onChange={handleChange} />
        <button type="submit">Submit</button>
      </form>
      <h1>Load Table:</h1>
      <input
        type="text"
        value={tableName}
        onChange={(e) => setTableName(e.target.value)}
      />
      <button onClick={fetchColumns}>Fetch Columns</button>
      <br />
      <h1>Columns:</h1>
      <ul>
        {columns.map((col) => (
          <li key={col.column_name}>
            {col.column_name} ({col.data_type})
            <input
              type="text"
              onClick={() => handleColumnSelect(col.column_name)}
            />
          </li>
        ))}
      </ul>
      <button onClick={handleConfirmClick}>Confirm</button>
      <h1>Selected Column Names:</h1>
      <ul>
        {columnNames.map((columnName) => (
          <li key={columnName}>{columnName}</li>
        ))}
      </ul>
      {selectedColumn && (
        <>
          <br />
          <h1>Edit Column:</h1>
          <p>Column Name: {selectedColumn}</p>
          <label>
            New Column Name:
            <input
              type="text"
              value={newColumnValues.columnName}
              onChange={handleColumnNameChange}
            />
          </label>
        </>
      )}
    </div>
  );
};

export default Users;
