import React, { useState } from "react";
import axios from "axios";

const App = () => {
  const [tableName, setTableName] = useState("");
  const [columns, setColumns] = useState([]);
  const [sourceDefinition, setSourceDefinition] = useState({});

  const handleTableNameChange = (event) => {
    setTableName(event.target.value);
  };

  const handleGetColumnsClick = () => {
    axios
      .get(`http://localhost:8800/?tableName=${tableName}`)
      .then((response) => {
        setColumns(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const handleSourceDefinitionChange = (event, index) => {
    const { name, value } = event.target;
    setSourceDefinition((prevState) => {
      const newColumns = [...prevState.columns];
      newColumns[index][name] = value;
      return { ...prevState, columns: newColumns };
    });
  };

  const handleDownloadClick = () => {
    axios
      .post("http://localhost:8800/", { sourceDefinition })
      .then((response) => {
        const url = window.URL.createObjectURL(new Blob([response.data]));
        const link = document.createElement("a");
        link.href = url;
        link.setAttribute("download", "output.json");
        document.body.appendChild(link);
        link.click();
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const renderColumns = () => {
    return columns.map((column, index) => (
      <tr key={index}>
        <td>{column.column_name}</td>
        <td>
          <input
            type="text"
            name="data_type"
            value={sourceDefinition.columns[index].data_type}
            onChange={(event) => handleSourceDefinitionChange(event, index)}
          />
        </td>
      </tr>
    ));
  };

  return (
    <div>
      <div>
        <label>Table name:</label>
        <input type="text" value={tableName} onChange={handleTableNameChange} />
        <button onClick={handleGetColumnsClick}>Next</button>
      </div>
      {columns.length > 0 && (
        <div>
          <table>
            <thead>
              <tr>
                <th>Column name</th>
                <th>Data type</th>
              </tr>
            </thead>
            <tbody>{renderColumns()}</tbody>
          </table>
          <button onClick={handleDownloadClick}>Download</button>
        </div>
      )}
    </div>
  );
};

export default App;
