// Frontend folder
import React, { useState } from "react";
import axios from "axios";

const App = () => {
  const [tableName, setTableName] = useState("");
  const [columns, setColumns] = useState([]);
  const [sourceDefinition, setSourceDefinition] = useState({
    Name: "",
    columns: [],
  });

  const fetchColumns = async () => {
    try {
      const response = await axios.get(`http://localhost:8800/`);
      console.log(response.data); // log response to console
      const columns = response.data.map((col) => ({
        column_name: col.column_name,
        data_type: col.data_type,
      }));
      setColumns(columns);
      setSourceDefinition({
        ...sourceDefinition,
        columns: columns,
      });
    } catch (error) {
      console.log(error);
    }
  };

  const handleDownload = async () => {
    try {
      const response = await axios.post(`http://localhost:8800/`, {
        tableName,
        sourceDefinition,
      });
      const url = window.URL.createObjectURL(
        new Blob([JSON.stringify(response.data)])
      );
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", "output.json");
      document.body.appendChild(link);
      link.click();
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div>
      <h1>Table Name:</h1>
      <input
        type="text"
        value={tableName}
        onChange={(e) => setTableName(e.target.value)}
      />
      <button onClick={fetchColumns}>Next</button>
      <div>
        {columns.map((column) => (
          <div key={column.column_name}>
            <p>Column Name: {column.column_name}</p>
            <p>Data Type: {column.data_type}</p>
          </div>
        ))}
        {columns.length > 0 && (
          <div>
            <p>Make changes to source definition as necessary:</p>
            <input
              type="text"
              placeholder="Column Name"
              onChange={(e) => {
                setSourceDefinition({
                  ...sourceDefinition,
                  columns: sourceDefinition.columns.map((col) =>
                    col.column_name === "id"
                      ? { ...col, column_name: e.target.value }
                      : col
                  ),
                });
              }}
            />
            <input
              type="text"
              placeholder="Data Type"
              onChange={(e) => {
                setSourceDefinition({
                  ...sourceDefinition,
                  columns: sourceDefinition.columns.map((col) =>
                    col.column_name === "id"
                      ? { ...col, data_type: e.target.value }
                      : col
                  ),
                });
              }}
            />
            <input
              type="number"
              placeholder="Data Length"
              onChange={(e) => {
                setSourceDefinition({
                  ...sourceDefinition,
                  columns: sourceDefinition.columns.map((col) =>
                    col.column_name === "id"
                      ? { ...col, data_length: e.target.value }
                      : col
                  ),
                });
              }}
            />
          </div>
        )}
        {sourceDefinition.columns.length > 0 && (
          <button onClick={handleDownload}>Download</button>
        )}
      </div>
    </div>
  );
};

export default App;
