import React, { useState, useRef } from "react";
import axios from "axios";

const Users = () => {
  const [tableName, setTableName] = useState("");
  const [columns, setColumns] = useState([]);
  const [sourceDefinition, setSourceDefinition] = useState({
    Name: "",
    columns: [],
  });
  const [tableStatus, setTableStatus] = useState();
  const inputRef = useRef(null);
  const fetchColumns = async () => {
    try {
      const response = await axios.get(`http://localhost:8800/`);
      const columns = response.data.map((col) => ({
        column_name: col.column_name,
        data_type: col.data_type,
      }));
      setTableStatus("");
      setColumns(columns);
      setSourceDefinition({
        ...sourceDefinition,
        columns: columns,
      });
    } catch (error) {
      console.log(error);
      setTableStatus("Table not found");
    }
  };

  const handleColumnNameChange = (event, i) => {
    const newColumns = [...sourceDefinition.columns];
    newColumns[i].column_name = event.target.value;
    setSourceDefinition({
      ...sourceDefinition,
      columns: newColumns,
    });

    inputRef.current.focus();
  };

  const handleDataTypeChange = (event, i) => {
    const newColumns = [...sourceDefinition.columns];
    newColumns[i].data_type = event.target.value;
    setSourceDefinition({
      ...sourceDefinition,
      columns: newColumns,
    });

    inputRef.current.focus();
  };

  const handleDownload = () => {
    const jsonData = JSON.stringify(sourceDefinition, null, 2);
    const file = new Blob([jsonData], { type: "application/json" });
    const a = document.createElement("a");
    const url = URL.createObjectURL(file);
    a.href = url;
    a.download = `${tableName}.json`;
    document.body.appendChild(a);
    a.click();
    setTimeout(() => {
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);
    }, 0);
  };

  return (
    <div>
      <h1>Table Name:</h1>
      <p>{tableStatus}</p>
      <form
        onSubmit={(event) => {
          event.preventDefault();
          setTableName(event.target[0].value);
        }}
      >
        <input type="text" />
        <button type="submit">Submit</button>
      </form>
      <p>Ready to fetch {tableName} table.</p>
      <h1>Load Table:</h1>
      <button onClick={fetchColumns}>Fetch Columns</button>
      <br />
      <h1>Columns:</h1>
      <ul>
        {columns.map((col, i) => (
          <li key={col.column_name}>
            <input
              type="text"
              value={col.column_name}
              onChange={(event) => handleColumnNameChange(event, i)}
            />
            (
            <input
              type="text"
              value={col.data_type}
              onChange={(event) => handleDataTypeChange(event, i)}
            />
            )
          </li>
        ))}
      </ul>
      <>
        <button onClick={handleDownload}>Download JSON</button>
      </>
    </div>
  );
};

export default Users;
